import React from 'react';
import Chart from "../Chart/chart";
import PageHelmet from "../common/Helmet";
import ScrollToTop from 'react-scroll-up';
import { FiChevronUp } from "react-icons/fi";
import Header from "../header/Header";
import Select from 'react-select';


const drinkOptions = [
    { value: '64', label: 'Espresso' },
    { value: '95', label: 'Coffee' },
    { value: '80', label: 'Red Bull' },
    { value: '45', label: 'Soda' },
    { value: '28', label: 'Green Tea' },
    { value: '47', label: 'Black Tea' },
  ];

const quantityOptions = [
    { value: 1, label: '1' },
    { value: 2, label: '2' },
    { value: 3, label: '3' },
    { value: 4, label: '4' },
    { value: 5, label: '5' },
];

const timeOptions = [
    { value: 5, label: '5:00 am' },
    { value: 6, label: '6:00 am' },
    { value: 7, label: '7:00 am' },
    { value: 8, label: '8:00 am' },
    { value: 9, label: '9:00 am' },
    { value: 10, label: '10:00 am' },
    { value: 11, label: '11:00 am' },
    { value: 12, label: '12:00 pm' },
    { value: 13, label: '1:00 pm' },
    { value: 14, label: '2:00 pm' },
    { value: 15, label: '3:00 pm' },
    { value: 16, label: '4:00 pm' },
    { value: 17, label: '5:00 pm' },
    { value: 18, label: '6:00 pm' },
    { value: 19, label: '7:00 pm' },
    { value: 20, label: '8:00 pm' },
    { value: 21, label: '9:00 pm' },
    { value: 22, label: '10:00 pm' },
    { value: 23, label: '11:00 pm' },
    { value: 0, label: '12:00 am' },
    { value: 1, label: '1:00 am' },
    { value: 2, label: '2:00 am' },
    { value: 3, label: '3:00 am' },
    { value: 4, label: '4:00 am' },
];

class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            drinkOption: null,
            quantityOption: null,
            timeOption: null,
            drinkList:[],
            data:[],
            bedtime: "10 pm",
            awaketime: "7 am +1"}
    
    
    this.handleDrinkChange = this.handleDrinkChange.bind(this)
    this.handleTimeChange = this.handleTimeChange.bind(this)
    this.handleQuantityChange = this.handleQuantityChange.bind(this)
    this.addDrink = this.addDrink.bind(this)
    this.removeDrink = this.removeDrink.bind(this)
    this.buildChart = this.buildChart.bind(this)
    }
    

    handleDrinkChange(value){
        this.setState({ drinkOption : value });
     }
     handleTimeChange(value){
        this.setState({ timeOption : value });
     }
     handleQuantityChange(value){
        this.setState({ quantityOption : value });
     }

    addDrink(){
        if(this.state.drinkOption && this.state.quantityOption && this.state.timeOption){
            //create item with uuid
            const newDrink={
                id: 1 + Math.random(),
                name: this.state.drinkOption.label,
                mgValue: this.state.drinkOption.value,
                quant: this.state.quantityOption.label,
                quantValue: this.state.quantityOption.value,
                time: this.state.timeOption.label,
                timeValue: this.state.timeOption.value
            };
            //copy of current list of items
            let list = this.state.drinkList;
            //add new item to list
            list.push(newDrink);
            //update state with new list 
            this.setState({drinkList: list}) 
            
        }
        else {
            alert("Please select all three fields.")}
    }
    removeDrink(id){
        const drinkList = [...this.state.drinkList]
        const updatedList = drinkList.filter(drink => drink.id !== id)
        this.setState({drinkList:updatedList})
    }
    
    buildChart(){
        // create hours for x axis
        let dataList = []
        for (let i = 0; i<36 ; i++){
            if(i==0){
                dataList.push({
                     "hour":`12 am`,
                    })
            }
            if(i==12){
                dataList.push({
                         "hour":`12 pm`,
                        })
            }
            if(i>0 &&i<12){
                dataList.push({
                 "hour":`${i} am`,
                })
            }
            if(i>12 && i<24){
                dataList.push({
                    "hour":`${i%12} pm`,
                   })
            }
            if(i==24){
                dataList.push({
                    "hour":`12 am +1`,
                   })
            }
            if(i>24){
                dataList.push({
                    "hour":`${i%24} am +1`,
                   })
            }
        }

        //create values for line series 1
        if(this.state.drinkList.length > 0){
            let drink1 = this.state.drinkList[0]
            let  t = 0
                for (let i=drink1.timeValue; i<36;i++){
                    let name = "drink1"
                    let obj = dataList[i]
                    let mgs = drink1.mgValue * drink1.quantValue
                    obj[name] =  (mgs * (0.5 ** (t/6))).toFixed(2)
                    t++ }
        }

        if(this.state.drinkList.length > 1){
            let drink2 = this.state.drinkList[1]
                let  t = 0
                for (let i=drink2.timeValue; i<36;i++){
                    let name = "drink2"
                    let obj = dataList[i]
                    let mgs = drink2.mgValue * drink2.quantValue
                    obj[name] =  (mgs * (0.5 ** (t/6))).toFixed(2)
                    t++ }
                
        }

        if(this.state.drinkList.length > 2){
            let drink3 = this.state.drinkList[2]
            let  t = 0
                for (let i=drink3.timeValue; i<36;i++){
                    let name = "drink3"
                    let obj = dataList[i]
                    let mgs = drink3.mgValue * drink3.quantValue
                    obj[name] =  (mgs * (0.5 ** (t/6))).toFixed(2)
                    t++ }
        }
        this.setState({data: dataList}) 
    }
    

    render(){

        let bedtimeCalc = this.state.data[22].drink1 + this.state.data[22].drink2 + this.state.data[22].drink3
        
    return (
        <>
            <PageHelmet pageTitle='Half Caff' />

            {/* Start Header Area  */}
            <Header headertransparent="header--transparent" colorblack="color--black" logoname="logo.png" />
            <div className="breadcrumb-area rn-bg-color ptb--10 bg_image bg_image--1" data-black-overlay="6">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-12">
                                <div className="breadcrumb-inner pt--100">
                                    <h2>Half Caff.</h2>
                                    <h4>Chart Caffeine's Half-Life</h4>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            {/* End Header Area  */}


            {/* Start Page Wrapper  */}
            <main className="page-wrapper">

                {/* Start Progress Bar Area   */}
                <div className="rn-progress-area ptb--50 bg_color--1">
                    <div className="container">
                        <div className="row justify-content-center row--15">
                          <div className="col-lg-3 col-md-6 col-12" >
                                    <Select
                                        value={this.state.drinkOption}
                                        placeholder = "Drink"
                                        onChange={(value) => this.handleDrinkChange(value)}
                                        options={drinkOptions}
                                    />
                            </div>
                            <div className="col-lg-2 col-md-2 col-12" >
                                    <Select
                                        value={this.state.quantityOption}
                                        placeholder = "Quantity"
                                        onChange={(value) => this.handleQuantityChange(value)}
                                        options={quantityOptions}
                                    />
                            </div > 
                            <div className="col-lg-2 col-md-2 col-12" >  
                                    <Select
                                        value={this.state.timeOption}
                                        placeholder = "Time"
                                        onChange={(value) => this.handleTimeChange(value)}
                                        options={timeOptions}
                                    />
                            </div > 
                            <div className="col-lg-2 col-md-2 col-12">
                                <button className="rn-button-style--2 btn-solid" onClick={ () => {this.addDrink();this.buildChart();}}>Add</button>  
                            </div>
                    </div>

                </div>
                </div>
                <div className="rn-progress-area bg_color--1">
                    <div className="container">
                        
                            {this.state.drinkList.map(drink => {
                            return(
                                <div key={drink.id} className="row justify-content-center ptb--10 ml--5 align-items-center row--15">
                                    <div className="col-lg-3 col-md-3 col-12l" >
                                        <h5>{drink.name}</h5> 
                                    </div>
                                    <div className="col-lg-2 col-md-3 col-12" >
                                        <h5>{drink.quant}</h5>
                                    </div>
                                    <div className="col-lg-2 col-md-3 col-12" >
                                        <h5>{drink.time}</h5>
                                    </div>
                                    <div className="col-lg-2 col-md-2 col-12" >
                                        <button className="rn-btn btn-solid" onClick={() => this.removeDrink(drink.id)}>-</button>
                                    </div>
                                </div>
                                )
                            })} 
                          
                    </div>
                </div>
                  
                <Chart  key={this.state.drinkList} 
                        drinkList = {this.state.drinkList} 
                        data = {this.state.data} 
                        bedtime = {this.state.bedtime} 
                        awaketime = {this.state.awaketime}
                        bedtimeCalc = {bedtimeCalc}/>
                
            </main>
            {/* End Page Wrapper  */}

            {/* Start Back To Top */}
            <div className="backto-top">
                <ScrollToTop showUnder={160}>
                    <FiChevronUp />
                </ScrollToTop>
            </div>
            {/* End Back To Top */}
        </>
        
    )}
}

export default App;
