import React from 'react';
//import './Track.css';


class Drink extends React.Component{
  constructor(props){
    super(props)
    this.addDrink = this.addDrink.bind(this)
    this.removeDrink = this.removeDrink.bind(this)
  }
  addDrink(){
    return this.props.onAdd(this.props.drink)
  }

  removeDrink(){
    return this.props.onRemove(this.props.drink)
  }


  renderAction(props){
    if (this.props.isRemoval){
      return <button className="Drink-action" onClick={this.removeDrink}>-</button>
    }
    else
      {return <button className="Drink-action" onClick={this.addDrink}>+</button>}
    }
  render() { 
    return (
      <div className="Drink">
       <div className="Drink-information">
        <h3>{this.props.track.name}</h3>
        <p>{this.props.track.artist} | {this.props.track.album}</p>
      </div>
      {this.renderAction()}
    </div>
    ); 
  }
}

export default Drink;
