import React from 'react';


class Drink extends React.Component{
  constructor(props){
    super(props)
    this.addDrink = this.addDrink.bind(this)
    this.removeDrink = this.removeDrink.bind(this)
  }
  addDrink(){
    return this.props.onAdd(this.props.drink)
  }
  removeDrink(){
    return this.props.onRemove(this.props.drink)
  }

  renderAction(props){
    if (this.props.isRemoval){
      return <button className="rn-btn btn-solid" onClick={this.removeDrink}>-</button>
    }
    }
  render() { 
    return (
        <div className="row justify-content-center align-items-center row--25">
            <div className="col-lg-3 col-md-3 col-12l" >
                <h5>Drink: Cappucino</h5> 
            </div>
            <div className="col-lg-2 col-md-3 col-12" >
                <h5>Quantity: 2</h5>
            </div>
            <div className="col-lg-2 col-md-3 col-12" >
                <h5>Time: 8:00 am</h5>
             </div>
            <div className="col-lg-2 col-md-2 col-12" >
                {this.renderAction()}
            </div>
       </div>
    ); 
  }
}

export default Drink;
