import React from 'react';
import Chart from "../Chart/chart";
import PageHelmet from "../common/Helmet";
import ScrollToTop from 'react-scroll-up';
import { FiChevronUp } from "react-icons/fi";
import Header from "../header/Header";
import Select from 'react-select';
import DrinkBox from '../DrinkBox/DrinkBox';

const drinkOptions = [
    { value: 'tea', label: 'Tea' },
    { value: 'espresso', label: 'Espresso' },
    { value: 'coffee', label: 'Coffee' },
    { value: 'energyDrink', label: 'Energy Drink' },
    { value: 'soda', label: 'Soda' },
  ];

const quantityOptions = [
    { value: 1, label: '1' },
    { value: 2, label: '2' },
    { value: 3, label: '3' },
    { value: 4, label: '4' },
    { value: 5, label: '5' },
];

const timeOptions = [
    { value: 0, label: '12:00 am' },
    { value: 1, label: '1:00 am' },
    { value: 2, label: '2:00 am' },
    { value: 3, label: '3:00 am' },
    { value: 4, label: '4:00 am' },
    { value: 5, label: '5:00 am' },
    { value: 6, label: '6:00 am' },
    { value: 7, label: '7:00 am' },
    { value: 8, label: '8:00 am' },
    { value: 9, label: '9:00 am' },
    { value: 10, label: '10:00 am' },
    { value: 11, label: '11:00 am' },
    { value: 12, label: '12:00 pm' },
    { value: 13, label: '1:00 pm' },
    { value: 14, label: '2:00 pm' },
    { value: 15, label: '3:00 pm' },
    { value: 16, label: '4:00 pm' },
    { value: 17, label: '5:00 pm' },
    { value: 18, label: '6:00 pm' },
    { value: 19, label: '7:00 pm' },
    { value: 20, label: '8:00 pm' },
    { value: 21, label: '9:00 pm' },
    { value: 22, label: '10:00 pm' },
    { value: 23, label: '11:00 pm' },
];

class App extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            drinkOption: null,
            quantityOption: null,
            timeOption: null,
            playlistTracks:[],}
  
        this.addDrink = this.addDrink.bind(this);
        this.removeDrink = this.removeDrink.bind(this); 
    }
    addDrink(drink){
        let drinks = this.state.playlistTracks
        if (drinks.find(savedDrink => savedDrink.id === drink.id)) {
          return }
        drinks.push(drink)
        this.setState({playlistTracks: drinks}) 
        };
      
    removeDrink(drink){
        let drinks = this.state.playlistTracks
        drinks = drinks.filter(currentDrink => currentDrink.id !== drink.id)
        this.setState({playlistTracks: drinks}) 
          };

    handleDrinkChange = drinkOption => {
        this.setState({ drinkOption });
      };
    handleQuantityChange = quantityOption => {
        this.setState({ quantityOption });
      };  
    
    handleTimeChange = timeOption => {
        this.setState({ timeOption });
      };      
    
    render (){

       const { quantityOption } = this.state;
       const { drinkOption } = this.state;
       const { timeOption } = this.state;
 
    return (
        <>
            <PageHelmet pageTitle='Half Caff' />

            {/* Start Header Area  */}
            <Header headertransparent="header--transparent" colorblack="color--black" logoname="logo.png" />
            <div className="breadcrumb-area rn-bg-color ptb--120 bg_image bg_image--1" data-black-overlay="6">
                    <div className="container">
                        <div className="row">
                            <div className="col-lg-12">
                                <div className="breadcrumb-inner pt--100">
                                    <h2>Caffeine Half-Life</h2>
                                    <h4>Chart The Half-Life of Caffeine</h4>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            {/* End Header Area  */}


            {/* Start Page Wrapper  */}
            <main className="page-wrapper">

                {/* Start Progress Bar Area   */}
                <div className="rn-progress-area ptb--120 bg_color--1">
                    <div className="container">
                        {/*</div><div className="row row--10" >*/}
                        <div className="row row--15" onSubmit={this.addItem}>
                          <div className="col-lg-4 col-md-6 col-12" >
                            <Select
                                 value={drinkOption}
                                 onChange={this.handleDrinkChange}
                                 options={drinkOptions}
                            />
                            </div>
                            <div className="col-lg-2 col-md-2 col-12" >
                                    <Select
                                        value={quantityOption}
                                        onChange={this.handleQuantityChange}
                                        options={quantityOptions}
                                    />
                            </div > 
                            <div className="col-lg-2 col-md-2 col-12" >  
                                    <Select
                                        value={timeOption}
                                        onChange={this.handleTimeChange}
                                        options={timeOptions}
                                    />
                            </div > 
                            <div className="col-lg-2 col-md-2 col-12">
                            <button className="rn-button-style--2 btn-solid" type="submit">Add</button>  
                            </div>
                    </div>
                    <ul className="theList">
                 
                    </ul>
                </div>
                </div>
                <div className="container">
                <DrinkBox />
                </div>
                <Chart />
            </main>
            {/* End Page Wrapper  */}

            {/* Start Back To Top */}
            <div className="backto-top">
                <ScrollToTop showUnder={160}>
                    <FiChevronUp />
                </ScrollToTop>
            </div>
            {/* End Back To Top */}
        </>
        
    )}
}

export default App;
