import React from 'react';


class Drink extends React.Component{
  constructor(props){
    super(props)
    this.addDrink = this.addDrink.bind(this)
    this.removeDrink = this.removeDrink.bind(this)
  }
  addDrink(){
    return this.props.onAdd(this.props.drink)
  }
  removeDrink(){
    return this.props.onRemove(this.props.drink)
  }

  renderAction(props){
    if (this.props.isRemoval){
      return <button className="rn-button-style--2 btn-solid" onClick={this.removeDrink}>-</button>
    }}
  render() { 
    return (
        <div className="row row--15">
        <div className="col-sm" >
            <h5>Drink: Cappucino, Quantity: 1, Time: 6 pm </h5>
        </div>
        <div className="col-sm" >
            {this.renderAction()}
        </div>
       </div>
    ); 
  }
}

export default Drink;
