import React from 'react'
import PageHelmet from "../component/common/Helmet";
import Breadcrumb from "../elements/common/Breadcrumb";
import ScrollToTop from 'react-scroll-up';
import { FiChevronUp } from "react-icons/fi";
import Header from "../component/header/Header";
import ProgressOne from "./progressbar/ProgressOne";
import ProgressTwo from "./progressbar/ProgressTwo";
import Footer from "../component/footer/Footer";

import * as am4core from '@amcharts/amcharts4/core'
import * as am4charts from '@amcharts/amcharts4/charts'
import am4themes_animated from "@amcharts/amcharts4/themes/animated";

am4core.useTheme(am4themes_animated);

class ProgressBar extends React.Component{

    componentDidMount(){
        let chart = am4core.create("chartdiv", am4charts.XYChart);
    
        chart.paddingRight = 30;
    
        let data = [];
        let caffHalfLife = 6
        let mg = 100;
        let mg2 = 100;
        let mg3  =0;
        let t = 0
        for (let t=0; t < 24; t++) {
            if (t>4){ 
                mg = mg * .5 ** (t/caffHalfLife) ; 
                mg2 = mg2*.5**((t-5)/caffHalfLife)
                mg3 = mg+mg2}
            else {
                mg3 = mg * .5 ** (t/caffHalfLife) }
          data.push({ time: t+8, name: "name" + t, value: mg3 });
        }

     
    
        chart.data = data;
        //chart.data2 = data2;

        let dateAxis = chart.xAxes.push(new am4charts.ValueAxis());
        dateAxis.renderer.grid.template.location = 0;
    
        let valueAxis = chart.yAxes.push(new am4charts.ValueAxis());
        valueAxis.tooltip.disabled = true;
        valueAxis.renderer.minWidth = 35;
    
        let series = chart.series.push(new am4charts.LineSeries());
        series.dataFields.valueX = "time";
        series.dataFields.valueY = "value";
    
        series.tooltipText = "{valueY.value}";
        chart.cursor = new am4charts.XYCursor();
    
        let scrollbarX = new am4charts.XYChartScrollbar();
        scrollbarX.series.push(series);
        chart.scrollbarX = scrollbarX;
    
        this.chart = chart;
      }
    
      componentWillUnmount() {
        if (this.chart) {
          this.chart.dispose();
        }
      }
    
    render (){
    return (
        <>
            <PageHelmet pageTitle='Half Caff' />

            {/* Start Header Area  */}
            <Header headertransparent="header--transparent" colorblack="color--black" logoname="logo.png" />
            {/* End Header Area  */}
            
            {/* Start Breadcrump Area */}
            <Breadcrumb title={ 'Caffeine Half-Life'} title2={'How long does coffee last?'}  />
            {/* End Breadcrump Area */}

            {/* Start Page Wrapper  */}
            <main className="page-wrapper">

                {/* Start Progress Bar Area   */}
                <div className="rn-progress-area ptb--120 bg_color--1">
                    <div className="container">
                        <div className="row row--35">
                            <div className="col-lg-6 col-md-6 col-12">
                                <ProgressOne ProgressStyle="progress-bar--1" />
                            </div>
                            <div className="col-lg-6 col-md-6 col-12 mt_sm--30">
                                <ProgressOne ProgressStyle="progress-bar--2" />
                            </div>
                        </div>
                    </div>
                </div>
                {/* End Progress Bar Area   */}

                {/* Start Progress Bar Area   */}
                <div className="rn-progress-area pb--120 bg_color--1">
                    <div className="container">
                    <div id="chartdiv" style={{ width: "100%", height: "500px" }}></div>
                    </div>
                </div>
                {/* End Progress Bar Area   */}

            </main>
            {/* End Page Wrapper  */}

            {/* Start Back To Top */}
            <div className="backto-top">
                <ScrollToTop showUnder={160}>
                    <FiChevronUp />
                </ScrollToTop>
            </div>
            {/* End Back To Top */}
            
            {/* Start Footer Area  */}
            
            {/* End Footer Area  */}

        </>
        
    )
}
}

export default ProgressBar;
