import React from 'react'
import * as am4core from '@amcharts/amcharts4/core'
import * as am4charts from '@amcharts/amcharts4/charts'
import am4themes_animated from "@amcharts/amcharts4/themes/animated";
am4core.useTheme(am4themes_animated);

class Chart extends React.Component{

    componentDidMount(){
        let chart = am4core.create("chartdiv", am4charts.XYChart);
        chart.data = [];

        // create hours for x axis
        for (let i = 0; i<32 ; i++){
            chart.data.push({
                 "hour":i,
                })
            }

        //create values for line series 1
        if(this.props.drinkList.length > 0){
            let drink1 = this.props.drinkList[0]
            let  t = 0
                for (let i=drink1.timeValue; i<32;i++){
                    let obj = chart.data[i]
                    let mgs = drink1.mgValue * drink1.quantValue
                    obj[drink1.name] =  mgs * (0.5 ** (t/6))
                    t++ }
        }


        let dateAxis = chart.xAxes.push(new am4charts.ValueAxis());
        dateAxis.renderer.minGridDistance = 60;
        dateAxis.startLocation = 0.5;
        dateAxis.endLocation = 0.5;
        dateAxis.baseInterval = {
        timeUnit: "hour",
        count: 1
        }   

        let valueAxis = chart.yAxes.push(new am4charts.ValueAxis());
        valueAxis.tooltip.disabled = true;

        if(this.props.drinkList.length > 0){
        let series = chart.series.push(new am4charts.LineSeries());
        series.dataFields.valueX = "hour";
        series.name = this.props.drinkList[0].name;
        series.dataFields.valueY = this.props.drinkList[0].name;
        series.tooltipHTML = "<img src='https://www.amcharts.com/lib/3/images/car.png' style='vertical-align:bottom; margin-right: 10px; width:28px; height:21px;'><span style='font-size:14px; color:#000000;'><b>{valueY.value}</b></span>";
        series.tooltipText = "[#000]{valueY.value}[/]";
        series.tooltip.background.fill = am4core.color("#FFF");
        series.tooltip.getStrokeFromObject = true;
        series.tooltip.background.strokeWidth = 3;
        series.tooltip.getFillFromObject = false;
        series.fillOpacity = 0.6;
        series.strokeWidth = 2;
        series.stacked = true;
        }
/*
        let series2 = chart.series.push(new am4charts.LineSeries());
        series2.name = "espresso";
        series2.dataFields.valueX = "hour";
        series2.dataFields.valueY = "espresso";
        series2.tooltipHTML = "<img src='https://www.amcharts.com/lib/3/images/motorcycle.png' style='vertical-align:bottom; margin-right: 10px; width:28px; height:21px;'><span style='font-size:14px; color:#000000;'><b>{valueY.value}</b></span>";
        series2.tooltipText = "[#000]{valueY.value}[/]";
        series2.tooltip.background.fill = am4core.color("#FFF");
        series2.tooltip.getFillFromObject = false;
        series2.tooltip.getStrokeFromObject = true;
        series2.tooltip.background.strokeWidth = 3;
        series2.sequencedInterpolation = true;
        series2.fillOpacity = 0.6;
        series2.stacked = true;
        series2.strokeWidth = 2;

        let series3 = chart.series.push(new am4charts.LineSeries());
        series3.name = "redbull";
        series3.dataFields.valueX = "hour";
        series3.dataFields.valueY = "redbull";
        series3.tooltipHTML = "<img src='https://www.amcharts.com/lib/3/images/bicycle.png' style='vertical-align:bottom; margin-right: 10px; width:28px; height:21px;'><span style='font-size:14px; color:#000000;'><b>{valueY.value}</b></span>";
        series3.tooltipText = "[#000]{valueY.value}[/]";
        series3.tooltip.background.fill = am4core.color("#FFF");
        series3.tooltip.getFillFromObject = false;
        series3.tooltip.getStrokeFromObject = true;
        series3.tooltip.background.strokeWidth = 3;
        series3.sequencedInterpolation = true;
        series3.fillOpacity = 0.6;
        series3.defaultState.transitionDuration = 1000;
        series3.stacked = true;
        series3.strokeWidth = 2;
*/

        chart.cursor = new am4charts.XYCursor();
        chart.cursor.xAxis = dateAxis;
        chart.scrollbarX = new am4core.Scrollbar();

        // Add a legend
        chart.legend = new am4charts.Legend();
        chart.legend.position = "top";

        // axis ranges
        let range = dateAxis.axisRanges.create();
        range.date = chart.data[0].hour;
        range.endDate = chart.data[chart.data.length-1].hour;
        range.axisFill.fill = chart.colors.getIndex(7);
        range.axisFill.fillOpacity = 0.2;

   /*     range.label.text = "Fines for speeding increased";
        range.label.inside = true;
        range.label.rotation = 90;
        range.label.horizontalCenter = "right";
        range.label.verticalCenter = "bottom";
*/
        let range2 = dateAxis.axisRanges.create();
        range2.date = chart.data[4].hour;
        range2.grid.stroke = chart.colors.getIndex(7);
        range2.grid.strokeOpacity = 0.6;
        range2.grid.strokeDasharray = "5,2";

/*
        range2.label.text = "Motorcycle fee introduced";
        range2.label.inside = true;
        range2.label.rotation = 90;
        range2.label.horizontalCenter = "right";
        range2.label.verticalCenter = "bottom";
        */
    }

    componentWillUnmount() {
        if (this.chart) {
          this.chart.dispose();
        }
      }




    render(){
      return(
        <div className="rn-progress-area pb--120 bg_color--1">
            <div className="container">
                <div id="chartdiv" style={{ width: "100%", height: "600px" }}></div>
            </div>
        </div>
      )}
}
      export default Chart
